
# base64 to tiff

import base64

data_base64 = ''

data_base64 = data_base64.encode()
data = base64.b64decode(data_base64)
open('image.tiff', 'wb').write(data)

# tiff to base64 and html

import base64

data = open('image.tiff', 'rb').read()
data_base64 = base64.b64encode(data)
data_base64 = data_base64.decode()

print(data_base64)

html = '<img src="data:image/tiff;base64,' + data_base64 + '">'
open('output-tiff.html', 'w').write(html)

# browsers don't display tiff so html can show broken image.
# you have to save from page to file and then you can see tiff.
